  name: 'Index',
  data() {
    return {
      filterThings: { ye: [
        { type: 'date', input: '' },
        { type: 'name', input: '' },
        { type: 'address', input: '' },
        { type: 'tag', input: '' },
        { type: 'startTime', input: '' },
        { type: 'endTime', input: '' }
      ] },
      perfectFilter: [{
      }],
      tableData: [{
        date: '2016-05-02',
        name: '俺是耕田滴',
